package com.mmall.controller.portal;import com.mmall.common.Const;import com.mmall.common.ResponseCode;import com.mmall.common.ServerResponse;import com.mmall.pojo.User;import com.mmall.service.IUserService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;import org.springframework.web.bind.annotation.RequestParam;import org.springframework.web.bind.annotation.RestController;import javax.servlet.http.HttpSession;/** * @author chenqiang * @create 2020-06-28 11:31 */@RestController@RequestMapping("/user")public class UserController {    @Autowired    private IUserService iUserService;    /**     * 用户登录     *     * @param username     * @param password     * @param session     * @return     */    @RequestMapping(value = "login.do", method = RequestMethod.POST)    public ServerResponse<User> login(String username, String password, HttpSession session) {        ServerResponse<User> response = iUserService.login(username, password);        if (response.isSuccess()) {            session.setAttribute(Const.CURRENT_USER, response.getData());        }        return response;    }    /**     * 用户登出     *     * @param session     * @return     */    @RequestMapping(value = "logout.do", method = RequestMethod.POST)    public ServerResponse<String> logout(HttpSession session) {        session.removeAttribute(Const.CURRENT_USER);        return ServerResponse.createBySuccess();    }    /**     * 用户注册     *     * @param user     * @return     */    @RequestMapping(value = "register.do", method = RequestMethod.POST)    public ServerResponse<String> register(User user) {        return iUserService.register(user);    }    /**     * 校验用户名和email     *     * @param str     * @param type     * @return     */    @RequestMapping(value = "check_valid.do", method = RequestMethod.POST)    public ServerResponse<String> checkValid(String str, String type) {        return iUserService.checkValid(str, type);    }    /**     * 获取用户信息     *     * @param session     * @return     */    @RequestMapping(value = "get_user_info.do", method = RequestMethod.POST)    public ServerResponse<User> getUserInfo(HttpSession session) {        User user = (User) session.getAttribute(Const.CURRENT_USER);        if (user != null) {            return ServerResponse.createBySuccess(user);        }        return ServerResponse.createByErrorMessage("用户没有登录，无法获取当前用户信息");    }    /**     * 获取问题     *     * @param username     * @return     */    @RequestMapping(value = "forget_get_question.do", method = RequestMethod.POST)    public ServerResponse<String> forgetGetQuestion(String username) {        return iUserService.selectQuestion(username);    }    /**     * 检查问题答案是否正确     *     * @param username     * @param question     * @param answer     * @return     */    @RequestMapping(value = "forget_check_answer.do", method = RequestMethod.POST)    public ServerResponse<String> forgetCheckAnswer(String username, String question, String answer) {        return iUserService.checkAnswer(username, question, answer);    }    /**     * 修改密码（未登录）     *     * @param username     * @param newPassword     * @param forgetToken     * @return     */    @RequestMapping(value = "forget_reset_password.do", method = RequestMethod.POST)    public ServerResponse<String> forgetResetPassword(String username, String newPassword, String forgetToken) {        return iUserService.forgetResetPassword(username, newPassword, forgetToken);    }    /**     * 修改密码（已登录）     *     * @param session     * @param oldPassword     * @param newPassword     * @return     */    @RequestMapping(value = "reset_password.do", method = RequestMethod.POST)    public ServerResponse<String> resetPassword(HttpSession session, String oldPassword, String newPassword) {        User user = (User) session.getAttribute(Const.CURRENT_USER);        if (user == null) {            return ServerResponse.createByErrorMessage("用户未登录");        }        return iUserService.resetPassword(oldPassword, newPassword, user);    }    /**     * 更新用户个人信息     *     * @param session     * @param user     * @return     */    @RequestMapping(value = "update_information.do", method = RequestMethod.POST)    public ServerResponse<User> updateInformation(HttpSession session, User user) {        User currentUser = (User) session.getAttribute(Const.CURRENT_USER);        if (currentUser == null) {            return ServerResponse.createByErrorMessage("用户未登录");        }        user.setId(currentUser.getId());        user.setUsername(currentUser.getUsername());        ServerResponse<User> response = iUserService.updateInformation(user);        if (response.isSuccess()) {            response.getData().setUsername(currentUser.getUsername());            session.setAttribute(Const.CURRENT_USER, response.getData());        }        return response;    }    /**     * 获取用户个人信息     *     * @param session     * @return     */    @RequestMapping(value = "get_information.do", method = RequestMethod.POST)    public ServerResponse<User> getInformation(HttpSession session) {        User currentUser = (User) session.getAttribute(Const.CURRENT_USER);        if (currentUser == null) {            return ServerResponse.createByErrorCodeMessage(ResponseCode.NEED_LOGIN.getCode(), "未登录，需要强制登录status=10");        }        return iUserService.getInformation(currentUser.getId());    }}